import {BasePlugin} from '@opencensus/core';

import * as shimmer from 'shimmer';


export class SimpleModulePlugin extends BasePlugin {
  constructor() {
    super('simple-module');
  }


  protected applyPatch() {
    this.logger.debug('apply path to simple module');

    shimmer.wrap(
        this.moduleExports, 'name', (orig) => () => 'patched-' + orig.apply());

    shimmer.wrap(this.moduleExports, 'value', (orig) => () => orig.apply() + 1);

    return this.moduleExports;
  }

  protected applyUnpatch(): void {
    shimmer.unwrap(this.moduleExports, 'name');
    shimmer.unwrap(this.moduleExports, 'value');
  }
}

const plugin = new SimpleModulePlugin();
export {plugin};
