import {BasePlugin} from '@opencensus/core';
import * as shimmer from 'shimmer';


export class MySimpleModulePlugin extends BasePlugin {
  constructor() {
    super('simple-module');
  }

  protected applyPatch() {
    shimmer.wrap(
        this.moduleExports, 'name',
        (orig) => () => 'my-patched-' + orig.apply());
    shimmer.wrap(this.moduleExports, 'value', (orig) => () => orig.apply() + 2);
    return this.moduleExports;
  }

  protected applyUnpatch(): void {
    shimmer.unwrap(this.moduleExports, 'name');
    shimmer.unwrap(this.moduleExports, 'value');
  }
}

const plugin = new MySimpleModulePlugin();
export {plugin};
