import {types} from '@opencensus/opencensus-core';
import {classes} from '@opencensus/opencensus-core';
import * as shimmer from 'shimmer';


export class MySimpleModulePlugin extends classes.BasePlugin {
  constructor() {
    super('simple-module');
  }

  // tslint:disable:no-any
  applyPatch(moduleExporters: any, tracer: types.Tracer, version: string) {
    this.setPluginContext(moduleExporters, tracer, version);
    // tslint:disable:no-any
    shimmer.wrap(
        moduleExporters, 'name', (orig) => () => 'my-patched-' + orig.apply());
    // tslint:disable:no-any
    shimmer.wrap(moduleExporters, 'value', (orig) => () => orig.apply() + 2);
    return moduleExporters;
  }

  applyUnpatch(): void {
    shimmer.unwrap(this.moduleExporters, 'name');
    shimmer.unwrap(this.moduleExporters, 'value');
  }
}

const plugin = new MySimpleModulePlugin();
export {plugin};
